allow:
Bash(echo '/**
 * Custom CSS for Docusaurus UI Upgrade
 * Implements improved typography, spacing, and theme customization
 */

/* CSS Custom Properties for Typography Scale */
:root {
  /* Base font settings */
  --ifm-font-family-base: ''''Inter'''', system-ui, -apple-system, sans-serif;
  --ifm-font-size-base: 100%; /* 16px */
  --ifm-line-height-base: 1.7;

  /* Typography scale */
  --ifm-h1-font-size: 2.5rem;
  --ifm-h2-font-size: 2rem;
  --ifm-h3-font-size: 1.5rem;
  --ifm-h4-font-size: 1.25rem;

  /* Body text */
  --ifm-font-size-text: 1.125rem; /* 18px */
  --ifm-font-size-small: 0.875rem; /* 14px */
}

/* CSS Custom Properties for Spacing System */
:root {
  /* Spacing scale based on 8px base unit */
  --ifm-spacing-horizontal: 1.5rem; /* 24px */
  --ifm-spacing-vertical: 1.5rem; /* 24px */

  /* Additional spacing units */
  --ifm-global-spacing: 1rem; /* 16px */
  --ifm-spacing-double: 2rem; /* 32px */
  --ifm-spacing-triple: 3rem; /* 48px */
  --ifm-spacing-half: 0.5rem; /* 8px */
}

/* CSS Custom Properties for Color Palette */
:root {
  /* Primary colors */
  --ifm-color-primary: #2563eb;
  --ifm-color-primary-dark: #1d4ed8;
  --ifm-color-primary-darker: #1e40af;
  --ifm-color-primary-darkest: #1e3a8a;
  --ifm-color-primary-light: #3b82f6;
  --ifm-color-primary-lighter: #60a5fa;
  --ifm-color-primary-lightest: #93c5fd;

  /* Secondary colors */
  --ifm-color-secondary: #64748b;
  --ifm-color-secondary-dark: #475569;

  /* Background colors */
  --ifm-color-background: #f8fafc;
  --ifm-color-emphasis-0: #ffffff;
  --ifm-color-emphasis-100: #f1f5f9;
  --ifm-color-emphasis-200: #e2e8f0;
  --ifm-color-emphasis-300: #cbd5e1;
  --ifm-color-emphasis-400: #94a3b8;
  --ifm-color-emphasis-500: #64748b;
  --ifm-color-emphasis-600: #475569;
  --ifm-color-emphasis-700: #334155;
  --ifm-color-emphasis-800: #1e293b;
  --ifm-color-emphasis-900: #0f172a;

  /* Text colors with better contrast */
  --ifm-color-content: #1e293b;
  --ifm-color-content-inverse: #ffffff;
}

/* Dark mode overrides */
[data-theme=''''dark'''']:root {
  /* Primary colors in dark mode */
  --ifm-color-primary: #60a5fa;
  --ifm-color-primary-dark: #3b82f6;
  --ifm-color-primary-darker: #2563eb;
  --ifm-color-primary-darkest: #1d4ed8;
  --ifm-color-primary-light: #93c5fd;
  --ifm-color-primary-lighter: #bfdbfe;
  --ifm-color-primary-lightest: #dbeafe;

  /* Background colors in dark mode */
  --ifm-color-background: #0f172a;
  --ifm-color-emphasis-0: #0f172a;
  --ifm-color-emphasis-100: #1e293b;
  --ifm-color-emphasis-200: #334155;
  --ifm-color-emphasis-300: #475569;
  --ifm-color-emphasis-400: #64748b;
  --ifm-color-emphasis-500: #94a3b8;
  --ifm-color-emphasis-600: #cbd5e1;
  --ifm-color-emphasis-700: #e2e8f0;
  --ifm-color-emphasis-800: #f1f5f9;
  --ifm-color-emphasis-900: #f8fafc;

  /* Text colors in dark mode */
  --ifm-color-content: #e2e8f0;
  --ifm-color-content-inverse: #0f172a;
}')
